            "pxt.json": "{\n    \"name\": \"audio-recording\",\n    \"description\": \"Record sound clips. micro:bit (V2) only\",\n    \"dependencies\": {\n        \"core\": \"*\"\n    },\n    \"files\": [\n        \"recording.ts\",\n        \"recording.cpp\",\n        \"shims.d.ts\"\n    ],\n    \"public\": true,\n    \"targetVersions\": {\n        \"target\": \"7.1.14\",\n        \"pxt\": \"11.3.4\"\n    },\n    \"weight\": 10,\n    \"icon\": \"/pxt-microbit-dogo/docs/static/libs/audio-recording.png\"\n}\n",
